import React, { Component } from 'react';
import Service  from  './Service';

const  userService  =  new  Service();
const  groupService  =  new  Service();

class  UserCreateUpdate  extends  Component {

    constructor(props) {
        super(props);
        this.dest = 'user'
        this.state  = {
            groups: [],
            nextPageURL:  ''
        };
        this.nextPage  =  this.nextPage.bind(this);

        this.handleSubmit = this.handleSubmit.bind(this);
    }

    nextPage(){
        var  self  =  this;
        groupService.getByURL(this.state.nextPageURL, this.dest).then((result) => {
            self.setState({ groups:  result.data, nextPageURL:  result.nextlink})
        });
    }

    componentDidMount(){
        var  self  =  this;

        var visited = [];
        var next = '';

        while(self.state.nextPageURL !== '')





        groupService.getAll(this.dest).then(function (result) {
            self.setState({ groups:  result.data, nextPageURL:  result.nextlink})
            next = self.state.nextPageURL
            while(!visited.includes(next)){
            visited.push(next)
            groupService.getByURL(self.state.nextPageURL, this.dest).then((result) => {
                self.setState({ groups:  result.data, nextPageURL:  result.nextlink})
                groupService.getAll(this.dest).then(function (result) {
                    var sel=document.getElementById("mySelect");
                        for (let i=0; i<self.state.groups.length; i++){
                            var option = document.createElement('option');
                            option.value = self.state.groups[i].name;
                            option.text = self.state.groups[i].name;
                            sel.appendChild(option);
                        }
                    });
                next = self.state.nextPageURL;
                self.nextPage();
            });
            }
        });

        groupService.getAll(this.dest).then(function (result) {
        var sel=document.getElementById("mySelect");

            for (let i=0; i<result.data.length; i++){
                var option = document.createElement('option');
                option.value = result.data[i].name;
                option.text = result.data[i].name;
                sel.appendChild(option);
            }
        });

        const { match: { params } } =  this.props;
        if(params  &&  params.pk)
        {
            userService.getById(params.pk, this.dest).then((c)=>{
                this.refs.username.value  =  c.username;
                this.refs.group.value  =  c.group;
            })
        }
    }

    handleCreate(){
        userService.create(
            {
            "username":  this.refs.username.value,
            "group":  this.refs.group.value
            },
            this.dest).then((result)=>{
                    alert("User created!");
            }).catch((e)=>{
                for (var i in e.response){console.log(i)}
                    alert(e.response.statusText);
            });
    }

    handleUpdate(pk){
        userService.update(
            {
            "pk":  pk,
            "username":  this.refs.username.value,
            "group":  this.refs.group.value
            },
            this.dest).then((result)=>{

                alert("User updated!");
            }).catch((e)=>{
                alert(e.response.data.name);
            })
        ;
    }

    handleSubmit(event) {
        const { match: { params } } =  this.props;
        if(params  &&  params.pk){
            this.handleUpdate(params.pk);
        }
        else
        {
            this.handleCreate();
        }
        event.preventDefault();
    }

    render() {

        return (
          <form onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label>
              Username:</label>
              <input className="form-control" type="text" ref='username' />

            <label>
              Group:</label><br></br>


              <select id="mySelect"  class="form-select form-select-lg mb-3" size="1" ref='group'>   
              </select><br></br>

              <a className="btn btn-primary mt-4" onClick=  {this.handleSubmit} href="/user/" >Submit</a>

            </div>
          </form>
        );

    }
}
export default UserCreateUpdate;
